
!function(a)
{
	"use strict";
	a('a[href*="#"]:not([href="#"])').click(function()
	{
		if(location.pathname.replace(/^\//,"")==this.pathname.replace(/^\//,"")&&location.hostname==this.hostname)
		{
			var n=a(this.hash);
			if(n=n.length?n:a("[name="+this.hash.slice(1)+"]"),n.length)
				return a("html, body").animate({scrollTop:n.offset().top-54
		},1e3,"easeInOutExpo"),!1
	}
}),a("body")
.scrollspy(
{
	target:"#mainNav",offset:54
}),a(".navbar-collapse>ul>li>a")
.click(function()
{
	a(".navbar-collapse").collapse("hide")}),a(window).scroll(function()
	{
		a("#mainNav").offset().top>100?a("#mainNav")
		.addClass("navbar-shrink"):a("#mainNav").removeClass("navbar-shrink")
	})
}(jQuery);

var current = 0;
var captionLength = 0;
var captionList = ['Computer Engineering','Army Institute Of Technology','Pune']


$(document).ready(function() {
  setInterval('cursorBlink()', 600);
  startTyping();
});

function startTyping() {
  caption = captionList[current];
  current = (current+1)%captionList.length;
  type();
}

function type() {
  $('#dynamic-desc').text('> '+caption.substr(0, captionLength++));
  if (captionLength < caption.length + 1) {
    setTimeout('type()',50);
  } else {
    setTimeout('erase()', 1000);
  }
}

function erase() {
  $('#dynamic-desc').text('> '+caption.substr(0, captionLength--));
  if (captionLength >= 0) {
    setTimeout('erase()', 50);
  } else {
    setTimeout('startTyping()',1000);
  }
}

function cursorBlink() {
      $('#cursor').animate({
          opacity: 0
      }, 'fast', 'swing').animate({
          opacity: 1
      }, 'fast', 'swing');
}
